import React from 'react';
import {connect} from 'react-redux';
import { show, hide, getWeather } from '../state/ducks/forms/actions';


const ReduxTest = ({isShow, show, getWeather, forms})=> {
console.log( isShow, show);

// hide();

show();

  return <div> redux test in console
    <div>
      <button onClick={getWeather}>
        Get Weater
      </button>
      <div>
        {forms.map((el, index) => <p key={index}> {el.isShow} </p>)}
      </div>
    </div>
  </div>
}

const mapStateToProps = ({forms}) => ({
  isShow: forms.isShowing
})

const mapDispatchToProps = {show, hide, getWeather};



export default connect(mapStateToProps, mapDispatchToProps )(ReduxTest);
