import * as types from "./types";
import {createReducer} from 'redux';

const initState = {
  temp : undefined,
  city : undefined,
  country : undefined ,
  sunrise : undefined,
  sunset : undefined,
  isShowing: false,
  error : undefined
}

const startReducer = createReducer(state = initState)({

  [types.SHOW]: () => ({
    isShowing: true
  }),

  [types.HIDE]: () => ({
    isShowing: false
  })
});

export default startReducer;
