import React from "react";
import { Form, Icon, Input as MainInput } from "antd";

// import "./Input.less";
// import "../FormComponents.less";

const SignInput = ({
  input,
  placeholder,
  meta: { touched, error },
  type,
  label,
  icon
}) => (
  <Form.Item
    hasFeedback
    validateStatus={touched ? (error ? "error" : "success") : ""}
    className={`form-item ${touched && error ? "has-error" : ""}`}
  >
    {label && <label>{label}</label>}

    <MainInput
      type={type}
      prefix={icon && <Icon type={icon} style={{ color: "rgba(0,0,0,.25)" }} />}
      placeholder={placeholder}
      {...input}
    />

    <p className="form-item_error ant-form-explain">{touched && error}</p>
  </Form.Item>
);

export default SignInput;
