import React, { useState } from 'react';
import AddTaskListForm from './AddTaskListForm';

const AddTaskList = () => {
  const [taskListForm, setTaskList] = useState(false);

  const showAddTaskListFormHandler = e => {
    setTaskList(!taskListForm);
    e.target.hidden = true;

  }
  return ( 
    <div>
      {taskListForm && <AddTaskListForm taskListForm={taskListForm} />}
      <button onClick={showAddTaskListFormHandler} 
              className={taskListForm? "close-form-btn": "add-list btn"}>
        <i className="fas fa-plus"></i>
      </button>
    </div>
  )
}

export default AddTaskList
