import React, { useState } from 'react'
import TaskList from './TaskList'
import './trello.css'
import AddTask from './AddTask'
import Loader from './Loader'

const Trello = () =>{
  const [allTasks, setAllTasks] = useState([
    {id: 0, title: 'Task 1', completed: false },
    {id: 1, title: 'Task 2', completed: false },
    {id: 2, title: 'Task 3', completed: false }
  ])

  const [loading, setLoading] = useState(true)

  const sleep = ms => new Promise(resolve=>{
    setTimeout(() => {
      resolve(setLoading(false))
    }, ms);
  })

  sleep(2000)


  function addTask(title){
    setAllTasks(allTasks.concat([{
      title,
      id : Date.now(),
      completed: false
    }]))
  }
  return (
    <div className="tasks-dashboard">
      {loading ?  <Loader/> : (
        <>
          <h3> Hello in TrELLO! </h3>
          <p className="">Create a task: </p>
          <AddTask onCreate={addTask} />
          <TaskList allTasks = {allTasks}/>
        </>
      )}
    </div>
  )
}


export default Trello;
