import * as types from "./types";
import createReducer from "../../../utils/createReducer";
import initialContacts from '../../../api/contacts';


const initialState = {
  contacts: initialContacts
}

const startReducer = createReducer(initialState)({

  [types.DELETE_CONTACT]: (state, {id}) => {
    console.log(id);

    const updateContacts = [...state.contacts];

    let indexOfDelete;
    updateContacts.forEach((contact, index) => {
      if(contact.id === id ){
        indexOfDelete = index;
      }
    })
    updateContacts.splice(indexOfDelete,1)

    return{
      ...state,
      contacts: updateContacts
    }
  },

  [types.FIND]: (state, {payload}) => {
      const findItems = []

      let regExp = new RegExp(`${payload}`, 'gi')

      state.contacts.forEach(item => {
      if (item.name.match(regExp)) {
        findItems.push(item.name);
      }
    });

    return{
      ...state,
      findItems
    }
  }

});

export default startReducer;
