import * as types from "./types";
import createReducer from "../../../utils/createReducer";

const initState = {
  temp : undefined,
  city : undefined,
  country : undefined ,
  sunrise : undefined,
  sunset : undefined,
  isShowing: false,
  error : undefined
}

const startReducer = createReducer(initialState)({
  [types.SHOW_SIDER]: () => ({
    isShowing: true
  }),

  [types.HIDE_SIDER]: () => ({
    isShowing: false
  })
});

export default startReducer;
