import * as types from "./types";
import axios from 'axios'
import {API_KEY} from '../../../App';

export const show = () =>( {
  type : types.SHOW
});


export const hide = () => ({
  type : types.HIDE
});


export const getWeather = (value) => dispatch =>{ 
  const resp  = axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${value}&appid=${API_KEY}`).then(res => dispatch({
    type : types.GET_WEATHER,
    payload : resp.data
  }))  
};

export const setCity = () => ({
  type :types.SET_CITY
})
