import React from "react";
import { connect } from "react-redux";
import "./contactList_style.css";
import {
  sort,
  deleteContact,
  edit,
  saveEdit
} from "../../state/ducks/search/actions";
import { useState } from "react";

const ContactList = ({ data, deleteContact, edit, saveEdit }) => {
  //
  const EditContact = ({ contact }) => {
    //

    const nameInputHandler = e => {
      setName(e.target.value);
      saveEdit(e.target.value);
    };

    const phoneInputHandler = e => {
      setphone(e.target.value);
      saveEdit(e.target.value);
    };

    return (
      <>
        <input name="name" onChange={nameInputHandler} value={name}></input>
        <input name="phone" onChange={phoneInputHandler} value={phone}></input>
      </>
    );
  };

  return (
    <div className="contactList-container">
      <h3> Contact List</h3>
      <div className="">
        {data.map((contact, index) => (
          <div key={index} className="contact">
            {!contact.edit && (
              <p key={contact.id}>
                {index + 1} {contact.name} : {contact.phoneNumber}
              </p>
            )}

            {contact.edit && <EditContact contact={contact} />}

            <div className="contact-buttons">
              <button
                onClick={() => {
                  contact.edit ? edit(contact.id) : edit(contact.id);
                }}
              >
                {contact.edit ? (
                  <i className="fas fa-save"></i>
                ) : (
                  <i className="fas fa-edit"></i>
                )}
              </button>
              <button
                onClick={() => {
                  deleteContact(contact.id);
                }}
              >
                <i className="fas fa-trash"></i>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const mapStateToProps = ({ contacts: { data } }) => ({ data });

const mapDispatchToProps = { sort, deleteContact, edit, saveEdit };

export default connect(mapStateToProps, mapDispatchToProps)(ContactList);
