import * as types from "./types";
import {createReducer} from 'redux';

const initState = {
  temp : undefined,
  city : undefined,
  country : undefined ,
  sunrise : undefined,
  sunset : undefined,
  isShowing: false,
  error : undefined
}

function startReducer ( state = initState, action) {
  switch(action.type){
    case "SHOW" :
      return { ...state, isShowing : action.payload.isShowing}
    case "HIDE" :
      return { ...state, isShowing : action.payload.isShowing}

  }
  return state;
}

export default startReducer;
