import React from 'react'
import TaskItem from './TaskItem';
import { connect } from 'react-redux';
import AddTask from './AddTask'

const TaskList = ( { taskItems, title, id, taskList  } ) => {

  const mouseOverHandler = (e) => {
    const currentClass = e.target.className
    let r = new RegExp(`active`, 'g')
    if(!currentClass.match(`${r}`)){
      currentClass.concat('active')
    }
    debugger
  }


  const style = {
    li:{
      display: 'block',
      maxWidth: '250px',
      width: '100%',
      padding: '0px',
      height: '24px',
      listStyleType: 'none',
    }
  }

  return (
    <div className="task-list-wrapper"
          id={id}
          onMouseOver={mouseOverHandler}
          // onMouseEnter={} 
          // onMouseLeave={} 
          >
      <div className="task-list">
        <>
          <h4 className='p-l-8'>{title}</h4>
        <div id={id}
             className={`${title.toLowerCase().match(/\w+/g).toLocaleString().replace(/[\s.,%]/g, '-')} task-list-container`}>
          <ul>
            {taskItems.map((task, index) => {
              return (
                <div key={index} index={index+1} >
                  <li style={style.li}
                      index={index+1} 
                      className='taskItem'
                      key={index}>
                        <TaskItem task={task} 
                                  key={task.id} 
                                  index={index} 
                                  column={id} />
                  </li>
                </div>
              )
            })
            }
          </ul>
          <AddTask idColumn={id}/>
        </div>
        </>
      </div>
    </div>
  )
}

const mapStateToProps = ({ allTasks:{ taskList } })=> ({taskList});

export default connect(mapStateToProps, null)(TaskList);
