import React from "react";
import { connect } from "react-redux";
import "./contactList_style.css";
import { sort } from "../../state/ducks/search/actions";
import { deleteContact } from "../../state/ducks/contacts/actions";

const ContactList = ({ contacts, sort, deleteContact }) => {
  sort(contacts);

  return (
    <div className="contactList-container">
      <h3> Contact List</h3>
      <div className="">
        {contacts.map((el, index) => (
          <div key={index} className="d-fx">
            <p key={el.id}>
              {index + 1} {el.name} : {el.phoneNumber}
            </p>
            <button
              onClick={() => {
                console.log(true);
                deleteContact(el.id);
              }}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

const mapStateToProps = ({ contacts: { contacts } }) => ({ contacts });

const mapDispatchToProps = { sort, deleteContact };

export default connect(mapStateToProps, mapDispatchToProps)(ContactList);
