import React, { useState } from "react";
import { connect } from "react-redux";
import Info from "../Info/Info";
import "./search_style.css";

const Search = ({ contacts }) => {
  const [contactName, setContactName] = useState("");

  const inputHandle = e => {
    setContactName(e.target.value);

    // const rexp = new RegExp(`/${e.target.value}/gi`);

    contacts.map(el => {
      console.log(el.name.match(/`Helena`/));
    });

    // console.log(
    //   contacts.map(el => {
    //     el.name.match(rexp);
    //   })
    // );
    // console.log(
    //   contacts.find(item => {
    //     let findItems = [];
    //     findItems = item.name.includes(`${e.target.value}`);
    //     return findItems;
    //   })
    // );
  };

  return (
    <div className="search-container">
      <Info />
      <div className="search">
        <input
          name="contactName"
          placeholder="Contact"
          value={contactName}
          onChange={inputHandle}
        />
      </div>
    </div>
  );
};

const mapStateToProps = ({ search: { contacts } }) => ({ contacts });

const mapDispatchToProps = {};

export default connect(mapStateToProps, mapDispatchToProps)(Search);
