import React, { useEffect, setState, useState } from 'react';
import {connect} from 'react-redux';
import { show, hide, getWeather, setCity } from '../state/ducks/forms/actions';


const ReduxTest = ({city, show, getWeather, forms, setCity})=> {

  const [state, setState] = useState({city: "Berlin"});


  const inputHandle = (e) => {
    console.log(e.target.value);
    setState({city: e.target.value})
  }

  console.log(state.city)

  return <div> redux test in console
    <div>
      <input name='city' 
      value={state.city}
             onChange={inputHandle}
             >
      </input>
      <button onClick={()=>getWeather()}>
        Get Weater
      </button>
      <div>
        <p>{getWeather().name}</p>
      </div>
    </div>
  </div>
}

const mapStateToProps = ({forms}) => ({
  isShow: forms.isShowing,
  city: forms.name
})

const mapDispatchToProps = {show, hide, getWeather, setCity};



export default connect(mapStateToProps, mapDispatchToProps )(ReduxTest);
