import * as types from "./types";
import axios from 'axios'

export const show = () =>( {
  type : types.SHOW
});


export const hide = () => ({
  type : types.HIDE
});

export const getWeather = () => async dispatch =>{ 
  const resp  = await axios.get('https://api.openweathermap.org/data/2.5/weather?q=Kiev&appid=9ea6825b8b5dae9d2dafb3fa6056126c');
  dispatch({
    type : types.GET_WEATHER,
    payload : resp.data
  })
  console.log(resp.data);

};
