import React from "react";
import { reduxForm, Field, getFormValues, formValues } from "redux-form";

import "./LogIn.css";

const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));
async function showResults(values) {
  await sleep(500); // simulate server latency

  window.alert(`You submitted:\n\n${JSON.stringify(values, null, 2)}`);
}

let LoginForm = ({ handleSubmit, submitting }) => {
  const state = getFormValues("login");

  console.log(formValues.form);

  return (
    <>
      <form className="login-form paper" onSubmit={handleSubmit(showResults)}>
        <div>
          <label> First Name </label>
          <Field name="firstName" component="input" placeholder="First Name" />
        </div>
        <div>
          <label> Last Name </label>
          <Field name="lastName" component="input" placeholder="Last Name" />
        </div>
        <div>
          <label> Email </label>
          <Field name="email" component="input" placeholder="Email" />
        </div>
        <button type="submit" disabled={submitting}>
          Submit
        </button>
      </form>
    </>
  );
};

LoginForm = reduxForm({
  form: "login",
  destroyOnUnmount: false
  // formValues: getFormValues("login")
})(LoginForm);

export default LoginForm;
