import React from 'react';
import startStore from '../state/store';
import * as action from '../state/ducks/forms/actions';
import * as reducer from '../state/ducks/forms/reducers';
import { actions } from '../state/ducks/forms';


const reduxTest = ()=> {

const store = startStore;

  function reducer ( state , action) {
    switch(action.type){
      case "SHOW" :
        return { ...state, isShowing : action.payload.isShowing}
      case "HIDE" :
        return { ...state, isShowing : action.payload.isShowing}

    }
    return state;
  }

  store.dispatch(action.show)
  console.log(store.getState());
  store.dispatch(action.hide)


  return <div> redux test in console</div>
}

export default reduxTest;
