import axios from "axios";
import { setHeader } from "./index";

export const USER_HOST = 'http://localhost:3010'


export const USER_API = `${USER_HOST}/users`;
const state = store.getState();
const token =
  (state.user && state.user.token) || localStorage.getItem("token");
export default {

  signin: credentials =>
    axios.post(`http://localhost:3010/login`, credentials).then(res =>res && res.data),

  me: () => {
    let config = {
      method: "GET",
      baseURL: `${USER_API}`,
      headers: {
        Accept: "application/json",
        Authorization: `Bearer ${token}`
      }
    };

    return axios(config).then(res => console.log(res))//res && res.data);
  }
};
