import React from 'react';
import {createStore} from 'redux';
import startStore from '../state/store';

const reduxTest = ()=> {

const store = startStore;
console.log(store.getState());

  function reducer ( state , action) {
    switch(action.type){
      case "SHOW" :
        return { ...state, isShowing : action.payload.isShowing}
      case "HIDE" :
        return { ...state, isShowing : action.payload.isShowing}

    }
    return state;
  }

  // const store = createStore(reducer);
  console.log(store.getState());


  const show = {
    type : "SHOW",
    payload : {
      isShowing: true
    }
  }


  const hide = {
    type : "HIDE",
    payload : {
      isShowing: false
    }
  }

  store.dispatch(show)
  console.log(store.getState());
  store.dispatch(hide)
  console.log(store.getState());


  return <div> redux test in console</div>
}

export default reduxTest;
