import React, { useState, useEffect } from 'react'
import './trello.css'
import Loader from './Loader'
import { connect } from 'react-redux'
import { initTask } from '../../../state/ducks/task/actions'
import Dashboard from './Dashboard'

const Trello = ({initTask}) => {

  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const timer = setTimeout(() => {
      initTask();
      setLoading(false)
    }
    , 2700);
    return () => clearTimeout(timer);
  }, [initTask]);

  let dragFlag
  let shiftX
  let shiftY
  let showClone
  const divEnterHadler = (e) => {
  }
  const divMouseDownHandler = (e) => {
    dragFlag = true
    let dragItem = e.target.cloneNode(true);

    e.target.closest('.testdnd').append(dragItem)
    dragItem.hidden=true
    debugger

    shiftX = e.clientX - e.target.getBoundingClientRect().left;
    shiftY = e.clientY - e.target.getBoundingClientRect().top;

    e.target.ondragstart = function() {
      return false;
    };
  }
  const divMouseMoveHandler = (e) => {
    showClone=true
    if(dragFlag && e.target.tagName==='SPAN'){
      document.body.append(e.target);

      e.target.className = 'dragable'

      moveAt(e.pageX, e.pageY);
    }
  function moveAt(pageX, pageY){
    e.target.style.left = pageX - shiftX +'px'
    e.target.style.top = pageY - shiftY +'px'
  }
}
const divMouseUp =e =>{
  e.target.className = ''
}


  return (
    <div className="dashboard-container">
      {loading ?  <Loader/> : (
        <>
          <h1 className='p-l-8'>Dashboard</h1>
          <div className="dashboard-content">
            <Dashboard />
            <div className='testdnd' id={1}
                  onMouseEnter={divEnterHadler}
                  onMouseDown={divMouseDownHandler}
                  onMouseMove={divMouseMoveHandler}
                  onMouseUp={divMouseUp}>
              <span>
                a
              </span>
            </div>
            <div className='testdnd' id={2}
                  onMouseEnter={divEnterHadler}
                  onMouseDown={divMouseDownHandler}
                  onMouseMove={divMouseMoveHandler}
                  onMouseUp={divMouseUp}>
              <span>
                b
              </span>
            </div>
          </div>
        </>
      )}
    </div>
  )
}



const mapDispatchToProps = { initTask };

export default connect(null, mapDispatchToProps)(Trello);
