import React from 'react'
import TaskItem from './TaskItem';
import { connect } from 'react-redux';
import AddTask from './AddTask'

const TaskList = ( { taskItems, title, id, taskList } ) => {
  // console.log((taskList.filter(el=> el.tasks === taskItems)));

  taskList.filter(el=> el.tasks === taskItems).map((task, index)=>{task.tasks.map(task=>{
    return <p>{task}</p>
  })})

  const style = {
    li:{
      display: 'block',
      maxWidth: '250px',
      width: '100%',
      padding: '0px',
      height: '24px',
      listStyleType: 'none',
    }
  }

  return (
    <div id={id} className="task-list-wrapper">
      <div className="task-list">
        <>
          <h4 className='p-l-8'>{title}</h4>
        <div id={id}
             className={`${title.toLowerCase().match(/\w+/g).toLocaleString().replace(/[\s.,%]/g, '-')} task-list-container`}>
          <ul>
            {taskList.filter(el=> el.tasks === taskItems).map((task, index) => {
              return (
                <li style={style.li}
                    index={index} 
                    className='taskItem'
                    key={index}>
                      <TaskItem task={task} 
                                key={task.id} 
                                index={index} 
                                column={id} />
                </li>
              )
            })
            }
          </ul>
          <AddTask idColumn={id}/>
        </div>
        </>
      </div>
    </div>
  )
}
