import * as types from "./types";
import createReducer from "../../../utils/createReducer";
import initialContacts from '../../../api/contacts';


const initialState = {
  contacts: initialContacts,
  findItems:[]
}

const startReducer = createReducer(initialState)({

  [types.SORT]: (state, {payload}) => {
    return{
      ...state,
      contacts: payload
    }
  },

  [types.FIND]: (state, {payload}) => {
    state.contacts.forEach(item => {
      if (item.name && item.name.includes(`${payload}`)) {
        state.findItems.push(item.name);
      }
    });

    return{
      ...state,
      findItems: state.findItems
    }
  }

});

export default startReducer;
