import {createStore} from 'redux';

const reduxTest = ()=> {

  const initState = {
    temp : undefined,
    city : undefined,
    country : undefined ,
    sunrise : undefined,
    sunset : undefined,
    isShowing: false,
    error : undefined
  }

  function reducer ( state = initState, action) {
    console.log(state);
    console.log(action);

  }

  const store = createStore(reducer, initState);

  const show = {
    type : "SHOW",
    payload : {
      isShowing: true
    }
  }


  const hide = {
    type : "HIDE",
    payload : {
      isShowing: false
    }
  }

  store.dispatch(show)
  return <div> redux test in console</div>
}

