import React from "react";
import { reduxForm, Field } from "redux-form";

import "./LogIn.css";
import { Button, Form } from "antd";
import { connect } from "react-redux";

let LoginForm = ({ handleSubmit, submitting }) => {
  //

  const showResults = values => {
    console.log(values.firstName);

    window.alert(`You submitted:\n\n${JSON.stringify(values, null, 2)}`);
  };

  return (
    <>
      <Form
        autoComplete="off"
        className="login-form paper"
        onSubmit={handleSubmit(showResults)}
      >
        {/* <form className="login-form paper" onSubmit={handleSubmit(showResults)}> */}
        <h1>Log in</h1>

        <div className="auth-form-field">
          <label> First Name </label>
          <Field
            name="firstName"
            component="input"
            type="text"
            placeholder="First Name"
          />
        </div>
        <div className="auth-form-field">
          <label> Password </label>
          <Field
            name="lastName"
            component="input"
            type="password"
            placeholder="Last Name"
          />
        </div>
        <div className="auth-form-field">
          <label> Email </label>
          <Field
            name="email"
            component="input"
            type="text"
            placeholder="Email"
          />
        </div>
        <Button disabled={submitting} htmlType="submit" type="primary">
          Sign in
        </Button>
        {/* </form> */}
      </Form>
    </>
  );
};

LoginForm = reduxForm({
  form: "login",
  destroyOnUnmount: false
})(LoginForm);

const mapStateToProps = state => ({
  initialValues: state.formInitialValues,
  formValues: getFormValues("login")(state)
});

const mapDispatchToProps = {}; //signIn

const enhance = compose(
  connect(mapStateToProps, mapDispatchToProps),
  reduxForm({ form: "login" })
);

export default LoginForm;
