import React from "react";

function Blog(props) {
  // const sidebar = (
  //   <ul>
  //     {props.posts.map((post) =>
  //       <li key={post.id}>
  //         {post.title}
  //       </li>
  //     )}
  //   </ul>
  // );
  const content = props.posts.map((post) =>
    <div key={post.id}>
      <h3>{post.title}</h3>
      <p>{post.content}</p>
    </div>
  );
  return (
    <div>
      <hr />
      {content}
    </div>
  );
}



export default Blog;
