import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import Info from "../Info/Info";
import "./search_style.css";
import { find, sort } from "../../state/ducks/search/actions";

const Search = ({ contacts, sort, find }) => {
  useEffect(() => {
    setFindItems(contacts);
  }, [contacts]);

  const [contactName, setContactName] = useState("");

  const [findItems, setFindItems] = useState(contacts);

  const inputHandle = e => {
    setContactName(e.target.value);

    sort(findItems);

    console.log(find(e.target.value));

    // contacts.forEach(item => {
    //   if (item.name && item.name.includes(`${e.target.value}`)) {
    //     setFindItems(findItems.push(item.name));
    //   }
    //   return setFindItems([]);
    // });

    // console.log(findItems);
  };

  return (
    <div className="search-container">
      <Info />
      <div className="search">
        <input
          name="contactName"
          placeholder="Contact"
          value={contactName}
          onChange={inputHandle}
        />
      </div>
      <hr />
      <br />
      {findItems.map(el => (
        <p key={el.id}>{el.name}</p>
      ))}
    </div>
  );
};

const mapStateToProps = ({ search: { contacts } }) => ({ contacts });

const mapDispatchToProps = { find, sort };

export default connect(mapStateToProps, mapDispatchToProps)(Search);
