import React from "react";
import { reduxForm, Field } from "redux-form";

import "./LogIn.css";
import { Button, Form } from "antd";

let LoginForm = ({ handleSubmit, submitting }) => {
  //

  const formSubmit = (e, values) => {
    e.preventDefault();

    showResults();
  };
  const showResults = values => {
    console.log("values: ", values);

    window.alert(`You submitted:\n\n${JSON.stringify(values, null, 2)}`);
  };

  return (
    <>
      <Form
        autoComplete="off"
        className="login-form paper"
        onSubmit={formSubmit}
      >
        {/* <form className="login-form paper" onSubmit={handleSubmit(showResults)}> */}
        <div className="auth-form-field">
          <label> First Name </label>
          <Field
            name="firstName"
            component="input"
            type="text"
            placeholder="First Name"
          />
        </div>
        <div className="auth-form-field">
          <label> Password </label>
          <Field
            name="lastName"
            component="input"
            type="password"
            placeholder="Password"
          />
        </div>
        <div className="auth-form-field">
          <label> Email </label>
          <Field
            name="email"
            component="input"
            type="text"
            placeholder="Email"
          />
        </div>
        <Button disabled={submitting} htmlType="submit" type="primary">
          Sign in
        </Button>
        {/* </form> */}
      </Form>
    </>
  );
};

LoginForm = reduxForm({
  form: "login",
  destroyOnUnmount: false
})(LoginForm);

export default LoginForm;
