import React, { useState, useEffect } from 'react'
import TaskList from './TaskList'
import './trello.css'
import AddTask from './AddTask'
import Loader from './Loader'
import { connect } from 'react-redux'
import { initTask } from '../../../state/ducks/task/actions'



const Trello = ({initTask}) => {
  useEffect(() => {
    const timer = setTimeout(() => console.log("Hello, World!"), 3000);
    return () => clearTimeout(timer);
  }, []);

  const [loading, setLoading] = useState(true)

  const sleep = ms => new Promise(resolve=>{
    setTimeout(() => {resolve()}, ms);
    clearTimeout(sleep)
    clearTimeout(resolve)
  })

  sleep(2800).then(()=>{
    setLoading(false)
    initTask()
    clearTimeout(sleep)
  }
);


  console.log('sukwa');



  return (
    <div className="tasks-dashboard">
      {loading ?  <Loader/> : (
        <>
          <h3> Hello in TrELLO! </h3>
          <p className="">Create a task: </p>
          <AddTask/>
          <TaskList/>
        </>
      )}
    </div>
  )
}



const mapDispatchToProps = { initTask};

export default connect(null, mapDispatchToProps)(Trello);
