import React, { useState } from 'react';
import AddTaskListForm from './AddTaskListForm';

const AddTaskList = () => {
  const [taskListForm, setTaskList] = useState(false);

  const addTaskListHandler = (e) => {
    let btn = e.target;
    setTaskList(!taskListForm)

    (taskListForm) ? btn.hide() : btn.show();
  }
  return ( 
    <div>
      <button onClick={addTaskListHandler} 
              sclassName="add-list btn">
        <i class="fas fa-plus"></i>
      </button>
      {taskListForm && <AddTaskListForm />}
    </div>
  )
}

export default AddTaskList
