import React from "react";
import { connect } from "react-redux";
import "./contactList_style.css";
import { sort } from "../../state/ducks/search/actions";

const ContactList = ({ contacts, sort }) => {
  sort(contacts);

  return (
    <div className="contactList-container">
      <h3> Contact List</h3>
      <div className="">
        {contacts.map((el, index) => (
          <div className="d-fx">
            <p key={el.id}>
              {index + 1} {el.name} : {el.phoneNumber}
            </p>
            <button>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

const mapStateToProps = ({ search: { contacts } }) => ({ contacts });

const mapDispatchToProps = { sort };

export default connect(mapStateToProps, mapDispatchToProps)(ContactList);
