import React, { useState } from 'react';
import {connect} from 'react-redux';
import { getWeather } from '../state/ducks/forms/actions';


const ReduxTest = ({getWeather })=> {

  const [cityvalue, setCityValue] = useState("");


  const inputHandle = (e) => {
    setCityValue( e.target.value );
  }

  const clickHandler = (cityvalue) =>{
    const data = getWeather(cityvalue);
    console.log(data);
  }


  return <div> redux test in console
    <div>
      <input name='city' 
      value={cityvalue}
             onChange={inputHandle}
             >
      </input>
      <button onClick={clickHandler(cityvalue)}>
        Get Weater
      </button>
      <div>
      </div>
    </div>
  </div>
}

const mapStateToProps = ({ forms }) => ({
  isShow: forms.isShowing,
  city: forms.name
})

const mapDispatchToProps = { getWeather };



export default connect(mapStateToProps, mapDispatchToProps )(ReduxTest);
