import * as types from "./types";
import createReducer from "../../../utils/createReducer";

const initialState = {
  temp : undefined,
  city : undefined,
  country : undefined ,
  sunrise : undefined,
  sunset : undefined,
  isShowing: false,
  error : undefined
}

const startReducer = createReducer(initialState)({
  [types.SHOW]: (state) => ({
    ...state,
    isShowing: true
  }),

  [types.HIDE]: (state) => {
    state.onLoad = true;
    return state
  }
});

export default startReducer;
