import React from "react";
import { useEffect } from "react";
import { connect } from "react-redux";
import Info from "../Info/Info";
import "./search_style.css";
import { find, sort, resetContacts } from "../../state/ducks/search/actions";

const Search = ({ findItems, contacts, find, resetContacts }) => {
  useEffect(() => {
    sort(findItems);
  }, [findItems]);

  const inputHandle = e => {
    find(e.target.value);
  };

  return (
    <div className="search-container">
      <Info />
      <div className="search">
        <input
          name="contactName"
          placeholder="Contact"
          onChange={inputHandle}
        />
      </div>
      <hr />
      {findItems && findItems.map((el, index) => <p key={index}>{el}</p>)}
      <button
        onClick={() => {
          resetContacts();
        }}
      >
        reset
      </button>
    </div>
  );
};

const mapStateToProps = ({ search: { contacts, findItems } }) => ({
  contacts,
  findItems
});

const mapDispatchToProps = { find, sort, resetContacts };

export default connect(mapStateToProps, mapDispatchToProps)(Search);
