import * as types from "./types";

export const sort = (arr) => ({
  type : types.SORT,
  payload: arr.sort((a, b) =>
  a.name !== b.name ? (a.name < b.name ? -1 : 1) : 0)
});


export const find = (payload) => ({
  type: types.FIND,
  payload
})

export const deleteContact = (id) => {

  return {
    type: types.DELETE_CONTACT,
    id
  }
};
