import React, { Component } from "react";
import { reduxForm, Field } from "redux-form";
import "./LogIn.css";
import { Button, Form } from "antd";
import { connect } from "react-redux";
import { compose } from "redux";
import { signIn } from "../../../state/ducks/user/actions";
// import Input from "../../components/FormsComponent/SignInput/SignInput";

class LoginForm extends Component {
  formSubmit = e => {
    e.preventDefault();

    const { handleSubmit, signIn } = this.props;

    handleSubmit(values => {
      //
      return signIn(values);
    })();
  };
  showResults = values => {
    window.alert(`You submitted:\n\n${JSON.stringify(values, null, 2)}`);
  };

  render() {
    return (
      <Form
        autoComplete="off"
        className="login-form paper"
        onSubmit={this.formSubmit}
      >
        <h1>Log in</h1>

        {/* <div className="auth-form-field">
          <label> User name </label>
          <Field
            type="text"
            icon="user"
            name="username"
            component="input"
            // component={Input}
            placeholder="First Name"
          />
        </div> */}
        <div className="auth-form-field">
          <label> Password </label>
          <Field
            name="password"
            component="input"
            type="password"
            placeholder="Last Name"
          />
        </div>
        <div className="auth-form-field">
          <label> Email </label>
          <Field
            name="email"
            component="input"
            type="text"
            placeholder="Email"
          />
        </div>
        <Button disabled={false} htmlType="submit" type="primary">
          Sign in
        </Button>
      </Form>
    );
  }
}
const mapDispatchToProps = { signIn };

const enhance = compose(
  connect(null, mapDispatchToProps),
  reduxForm({ form: "login" })
);

export default enhance(LoginForm);
