import React from "react";

class Blog extends React.Component {
  constructor(props) {
    super(props);

    const sidebar = (
      <ul>
        {props.posts.map(post => (
          <li key={post.id}>{post.title}</li>
        ))}
      </ul>
    );
    const content = props.posts.map(post => (
      <div key={post.id}>
        <h3>{post.title}</h3>
        <p>{post.content}</p>
      </div>
    ));
  }

  render() {
    return (
      <div>
        {sidebar}
        <hr />
        {content}
      </div>
    );
  }
}

export default Blog;
