import React, { useState } from 'react';
import AddTaskListForm from './AddTaskListForm';

const AddTaskList = () => {
  const [taskListForm, setTaskList] = useState(false);

  const showAddTaskListFormHandler = e => {
    setTaskList(!taskListForm);
    if(taskListForm === false){
      e.target.hidden=taskListForm
    }else{
      e.target.hidden = true;
    }
  }
  return ( 
    <div>
      {taskListForm && <AddTaskListForm taskListForm={taskListForm} setTaskList={setTaskList} />}
      <button onClick={showAddTaskListFormHandler} 
              className="add-list btn">
        <i className="fas fa-plus"></i>
      </button>
    </div>
  )
}

export default AddTaskList
