import * as types from "./types";
// import api from "../../../api";
import users from '../../../api/users';

export const userSignIn = ({ token }) => { //let take users by timeout
  if (token) {
    localStorage.setItem("token", token);

    return {
      type: types.SIGN_IN,
      token
    };
  }
};

export const setMe = user => ({
  type: types.SET_ME,
  user: {
    ...user,
    permissions: user.allPermissions
  }
});

export const signIn = (data) => dispatch => {
  let apiUser = setTimeout(() => {
    return users
  }, 500);
  console.log(data);
  console.log(users);
  apiUser().then(data => console.log(data))


}

