import React  from 'react';
// import TaskList from './TaskList';
import { connect } from 'react-redux';
import AddTaskList from './AddTaskList';
import AddTask from './AddTask';
import TaskItem from './TaskItem';

const Dashboard = ({taskList}) => {

  const TaskList = ( { taskItems, title, id } ) => {

    const style = {
      li:{
        display: 'block',
        maxWidth: '250px',
        width: '100%',
        padding: '0px',
        height: '24px',
        listStyleType: 'none',
      }
    }

    return (
      <div id={id} className="task-list-wrapper">
        <div className="task-list">
          <>
            <h4 className='p-l-8'>{title}</h4>
          <div id={id}
               className={`${title.toLowerCase().match(/\w+/g).toLocaleString().replace(/[\s.,%]/g, '-')} task-list-container`}>
            <ul>
              {taskItems.map((task, index) => {
                return (
                  <li style={style.li}
                      index={index} 
                      className='taskItem'
                      key={index}>
                        <TaskItem task={task} 
                                  key={task.id} 
                                  index={index} 
                                  column={id} />
                  </li>
                )
              })
              }
            </ul>
            <AddTask idColumn={id}/>
          </div>
          </>
        </div>
      </div>
    )
  }

  return(
    <>
    <div className="board">
      {taskList.map((column, index)=> {
        return <TaskList title={column.title}
                         id={column.id}
                         key={index}
                         taskItems={column.tasks}/>
      })}
    </div>
    <AddTaskList />
  </>
  )
}



const mapStateToProps = ({ allTasks:{ taskList } })=> ({taskList});

export default connect(mapStateToProps, null)(Dashboard);
