import React, { useState, useEffect } from 'react'
import './trello.css'
import Loader from './Loader'
import { connect } from 'react-redux'
import { initTask } from '../../../state/ducks/task/actions'
import Dashboard from './Dashboard'

const Trello = ({initTask}) => {

  const [loading, setLoading] = useState(true)

  useEffect(() => {
    const timer = setTimeout(() => {
      initTask();
      setLoading(false)
    }
    , 2700);
    return () => clearTimeout(timer);
  }, [initTask]);

  const divEnterHadler = (e) => {
    console.log(e.target);

  }
  const divMouseDownHandler = (e) => {
    console.log(e.target);

  }

  return (
    <div className="dashboard-container">
      {loading ?  <Loader/> : (
        <>
          <h1 className='p-l-8'>Dashboard</h1>
          <div className="dashboard-content">
            <Dashboard />
            <div className='testdnd' id={1}
                  onMouseEnter={divEnterHadler}
                  onMouseDown={divMouseDownHandler}>
              <span>
                a
              </span>
            </div>
            <div className='testdnd' id={2}>
              <span>
                b
              </span>
            </div>
          </div>
        </>
      )}
    </div>
  )
}



const mapDispatchToProps = { initTask };

export default connect(null, mapDispatchToProps)(Trello);
