import React from 'react'
import TaskItem from './TaskItem';
import { connect } from 'react-redux';
import AddTask from './AddTask'

const TaskList = ( {title, id, allTasks } ) => {

  // const catchTaskItem = e => {
  //   const targetTask = e.target
  //   const columnid = id
  //   const elemBelow = document.elementFromPoint(e.clientX, e.clientY);
  //   // console.log(elemBelow.children[0].children[1].id)
  //   // console.log(id);
  // }

  return (
    <div className="task-list-wrapper"
          // onMouseOver={catchTaskItem}
          >
      <div className="task-list">
          <h4 className='p-l-8'>{title}</h4>
        <div id={id}
             className={`${title.toLowerCase().match(/\w+/g).toLocaleString().replace(/[\s.,%]/g, '-')} task-list-container`}>
          <ul>
            {allTasks.map((task, index) => {
              if(task.status === id){
                return (
                <li className='taskItem'
                    key={index}>
                  <TaskItem task={task} key={task.id} index={index} column={id} />
                </li>
                )
              }
            })
          }
          </ul>
          <AddTask idColumn={id}/>
        </div>
      </div>
    </div>
  )
}


const mapStateToProps = ({ allTasks: {allTasks} })=> ({allTasks});

export default connect(mapStateToProps, null)(TaskList);
