import * as types from "./types";
// import api from "../../../api";
import users from '../../../api/users'

export const userSignIn = ({ token }) => {
  if (token) {
    localStorage.setItem("token", token);

    return {
      type: types.SIGN_IN,
      token
    };
  }
};

export const setMe = user => ({
  type: types.SET_ME,
  user: {
    ...user,
    permissions: user.allPermissions
