import * as types from "./types";
// import api from "../../../api";
import users from '../../../api/users';

export const userSignIn = ({ user }) => { //let take users by timeout
  if (user) {
    localStorage.setItem("user", user);

    return {
      type: types.SIGN_IN,
      user
    };
  }
};

export const setMe = user => ({
  type: types.SET_ME,
  user: {
    ...user,
    permissions: user.allPermissions
  }
});

export const signIn = (data) => dispatch => {

  const apiUser = ms => new Promise(resolve=> {setTimeout(resolve, ms);});

  async function sin(data, ms) {
    await apiUser(ms).then(()=> data)
  }

  sin(users, 1500).then(()=> 
    users.forEach((user)=>{
      if(user.password===data.password && (user.nameOrMail===data.username  || user.nameOrMail===data.email) ){
        console.log('log in ')
        dispatch(userSignIn)
    }else return;
    })

    // }
  ) 
}

