import React, { useState, useEffect } from 'react'
import './trello.css'
import Loader from './Loader'
import { connect } from 'react-redux'
import { initTask } from '../../../state/ducks/task/actions'
import Dashboard from './Dashboard'

const Trello = ({initTask}) => {

  const [loading, setLoading] = useState(true)

  useEffect(() => {
    let storeTaskList = JSON.parse(localStorage.getItem("tasks-list"))||[];
    let storeTask = JSON.parse(localStorage.getItem("tasks"))||[];

    const timer = setTimeout(() => {
      initTask(storeTaskList, storeTask);
      setLoading(false)
    }
    , 2700);
    return () => clearTimeout(timer);
  }, [initTask]);

  return (
    <div className="dashboard-container">
      {loading ?  <Loader/> : (
        <>
          <h1 className='p-l-8'>Dashboard</h1>
          <div className="dashboard-content">
            <Dashboard />
          </div>
          <button onClick={()=>{}}></button>
        </>
      )}
    </div>
  )
}



const mapDispatchToProps = { initTask };

export default connect(null, mapDispatchToProps)(Trello);
