import React from "react";
import { connect } from "react-redux";
import "./contactList_style.css";
import { sort, deleteContact, edit } from "../../state/ducks/search/actions";
import { useState } from "react";

const ContactList = ({ data, deleteContact, edit }) => {
  const [editFlag, setEditFlag] = useState(false);

  const openEditField = () => {
    setEditFlag(!editFlag);
  };

  const EditContact = () => {
    return <p>edit field!</p>;
  };

  return (
    <div className="contactList-container">
      <h3> Contact List</h3>
      <div className="">
        {data.map((el, index) => (
          <div key={index} className="contact">
            <p key={el.id}>
              {index + 1} {el.name} : {el.phoneNumber}
            </p>
            {editFlag && <EditContact />}
            <div className="contact-buttons">
              <button
                onClick={() => {
                  openEditField(el.id);
                }}
              >
                <i className="fas fa-edit"></i>
              </button>
              <button
                onClick={() => {
                  deleteContact(el.id);
                }}
              >
                <i className="fas fa-trash"></i>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const mapStateToProps = ({ contacts: { data } }) => ({ data });

const mapDispatchToProps = { sort, deleteContact, edit };

export default connect(mapStateToProps, mapDispatchToProps)(ContactList);
