import React, { Component } from "react";
import { reduxForm, Field } from "redux-form";
import "./LogIn.css";
import { Button, Form, message } from "antd";
import { connect } from "react-redux";
import { compose } from "redux";
import { signIn } from "../../../state/ducks/user/actions";
// import Input from "../../components/FormsComponent/SignInput/SignInput";

class LoginForm extends Component {
  //
  formSubmit = e => {
    e.preventDefault();

    console.log(this.props.user);

    const { handleSubmit, signIn } = this.props;

    handleSubmit(values => {
      //
      return signIn(values).then(() => {
        JSON.parse(localStorage.getItem("user"))
          ? message.success("Вы успешно вошли")
          : message.error("Что-то пошло не так");
      });
    })();
  };

  showResults = user => {
    console.log(user);
  };

  render() {
    return (
      <Form
        autoComplete="off"
        className="login-form paper"
        onSubmit={this.formSubmit}
      >
        <h1>Log in</h1>

        {/* <div className="auth-form-field">
          <label> User name </label>
          <Field
            type="text"
            icon="user"
            name="username"
            component="input"
            // component={Input}
            placeholder="First Name"
          />
        </div> */}

        <div className="auth-form-field">
          <label> Email / name </label>
          <Field
            name="nameOrMail"
            component="input"
            type="text"
            placeholder="Email or username"
          />
        </div>
        <div className="auth-form-field">
          <label> Password </label>
          <Field
            name="password"
            component="input"
            type="password"
            placeholder="Password"
          />
        </div>
        <Button disabled={false} htmlType="submit" type="primary">
          Sign in
        </Button>
      </Form>
    );
  }
}

const mapStateToProps = user => ({ user });

const mapDispatchToProps = { signIn };

const enhance = compose(
  connect(mapStateToProps, mapDispatchToProps),
  reduxForm({ form: "login" })
);

export default enhance(LoginForm);
